  selector: 'app-browse-card',
  templateUrl: './browse-card.component.html',
  styleUrls: ['./browse-card.component.css'],
})
export class BrowseCardComponent implements OnInit {
  @Input() bgColor!: string;
  @Input() textColor!: string;
  @Input() title!: string;
  constructor() {}

  ngOnInit(): void {}
}
