import { Component, OnInit, Input } from '@angular/core';
import { Router } from '@angular/router';
import { SongDataService } from 'src/app/services/song-data.services';

@Component({
  selector: 'app-song-card',
  templateUrl: './song-card.component.html',
  styleUrls: ['./song-card.component.css']
})
export class SongCardComponent implements OnInit{

  @Input() public playlistThumbnail!: string;
  @Input() public title!: string;
  @Input() public description!: string;
  @Input() public link!: string;
  @Input() public song_id!: any;

  constructor(private router: Router, private sd: SongDataService) {}

  ngOnInit(): void {}

  onNavigateToSong() {

    this.router.navigateByUrl(`/song/${this.song_id}`)
    setTimeout(() => {}, 1000)
    this.sd.songData.next({
    thumbnail: this.playlistThumbnail,
    title: this.title,
    description: this.description,
    link: this.link,


    })

  }

}
<div class="spotify__song-card" (click)="onNavigateToSong()">
    <div class="spotify_song-card__thumbnail">

        <img [src]="playlistThumbnail" width="120" alt="thumbnail" />
    </div>
    <div class="spotify_song-card__title">
        {{title}} {{song_id}}</div>
    <div class="spotify_song-card__description">
        {{description}}</div>


</div>
